import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import Header from '../components/Header';
import './Profile.css';

const Profile = () => {
  const user = JSON.parse(localStorage.getItem('user'));
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    subscription: false,
    profileImage: '',
    password: '',
  });
  const [success, setSuccess] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const response = await fetch(`http://127.0.0.1:5000/profile?email=${user.email}`);
        const data = await response.json();
        if (!response.ok) throw new Error(data.message);
        setFormData(data);
      } catch (err) {
        setError(err.message);
      }
    };
    fetchProfile();
  }, [user.email]);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData({ ...formData, [name]: type === 'checkbox' ? checked : value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');

    try {
      const response = await fetch('http://127.0.0.1:5000/profile', {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData),
      });
      const data = await response.json();
      if (!response.ok) throw new Error(data.message);
      setSuccess('Profile updated successfully!');
      if (formData.email !== user.email) {
        localStorage.setItem('user', JSON.stringify({ ...user, email: formData.email }));
      }
    } catch (err) {
      setError(err.message);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('user'); // Remove user data from localStorage
    navigate('/signin'); // Redirect to sign-in page
  };

  return (
    <div>
      <Header />
      <div className="ProfilePage">
        <h2>Profile</h2>
        <form onSubmit={handleSubmit}>
          <label>First Name</label>
          <input type="text" name="firstName" value={formData.firstName} onChange={handleChange} />

          <label>Last Name</label>
          <input type="text" name="lastName" value={formData.lastName} onChange={handleChange} />

          <label>Email</label>
          <input type="email" name="email" value={formData.email} onChange={handleChange} />

          <label>Profile Image URL</label>
          <input type="text" name="profileImage" value={formData.profileImage} onChange={handleChange} />

          <label>New Password</label>
          <input type="password" name="password" placeholder="Enter new password" onChange={handleChange} />

          <label>
            <input type="checkbox" name="subscription" checked={formData.subscription} onChange={handleChange} />
            Subscribe to email updates
          </label>

          <button type="submit">Update Profile</button>
        </form>

        <button className="logout-btn" onClick={handleLogout}>Logout</button>

        {success && <p className="success">{success}</p>}
        {error && <p className="error">{error}</p>}
      </div>
    </div>
  );
};

export default Profile;
